import React, { Component } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

class Recipes extends Component {
  constructor(props) {
    super(props);

    this.state = {
      recipes: null
    };
  }

  async componentDidMount() {
    const recipes = (await axios.get("http://localhost:8081/")).data;


    var cardStyles = ['tangerine', 'lime', 'moonstone-blue', 'light-blue', 'dust-pink']

    var selectedStyle = cardStyles[Math.floor(Math.random() * cardStyles.length)]

    var styleClass = "card text-white mb-3" + selectedStyle
    console.log(styleClass)

    this.setState({
      recipes,
      styleClass
    });
  }

  render() {


    return (
      <div className="container">
        <div className="row">
          <div className="col-sm-12 col-md-4 col-lg-3">
            <Link to="/create-recipe">
              <div className="card text-white mb-3">
                <div className="card-body lavender-gray">
                  <h4 className="card-title">+ New Recipe</h4>
                  <p className="card-text">
                    Click here to add your own recipe!
                  </p>
                </div>
              </div>
            </Link>
          </div>
          {this.state.recipes === null && <p>Loading recipes...</p>}
          {this.state.recipes &&
            this.state.recipes.map(recipe => (
              <div key={recipe.id} className="col-sm-12 col-md-4 col-lg-3">
                <Link to={`/recipe/${recipe.id}`}>
                  <div className="card text-white  mb-3">
                    <div className="card-header">
                      Comments: {recipe.comments}
                    </div>
                    <div className="card-body">
                      <h4 className="card-title">{recipe.title}</h4>
                      <p className="card-text">{recipe.description}</p>
                    </div>
                  </div>
                </Link>
              </div>
            ))}
        </div>
      </div>
    );
  }
}

export default Recipes;
