import React, { Component } from "react";
import { withRouter } from "react-router-dom";
import auth0Client from "../../Auth";
import axios from "axios";

class CreateRecipe extends Component {
  constructor(props) {
    super(props);

    this.state = {
      disabled: false,
      title: "",
      image: null,
      description: "",
      ingredients: ""
    };
  }

  updateTitle(value) {
    this.setState({
      title: value
    });
  }

  updateImage(file) {
    this.setState({ 
      image: file.target.files[0] 
    });
  }
  updateDescription(value) {
    this.setState({
      description: value
    });
  }

  updateIngredients(value) {
    this.setState({
      ingredients: value
    });
  }

  async submit(file) {
    file.preventDefault();
    const formData = new FormData();
    formData.append("myImage", this.state.image);
    const config = {
      headers: {
        "content-type": "multipart/form-data"
      }
    };

    this.setState({
      disabled: true
    });

    await axios.post(
      "http://localhost:8081",
      {
        title: this.state.title,
        image: this.state.image,
        description: this.state.description,
        ingredients: this.state.ingredients
      },
      {
        headers: { Authorization: `Bearer ${auth0Client.getIdToken()}` }
      }
    );

    axios
      .post("/upload", formData, config)
      .then(response => {
        alert("The file is successfully uploaded");
      })
      .catch(error => {});

    this.props.history.push("/");
  }

  render() {
    return (
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="card">
              <div className="card-header">
                <h3>Create new recipe</h3>
              </div>
              <div className="card-body text-left">
                <div className="form-group">
                  <label htmlFor="title">Title:</label>
                  <input
                    disabled={this.state.disabled}
                    type="text"
                    onBlur={e => {
                      this.updateTitle(e.target.value);
                    }}
                    className="form-control"
                    placeholder="Enter recipe title"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="image">Image:</label>
                  <input
                    disabled={this.state.disabled}
                    type="file"
                    name="myImage"
                    onBlur={e => {
                      this.updateImage();
                    }}
                    className="form-control"
                    placeholder="Please upload an image of your recipe"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="ingredients">Ingredients:</label>
                  <input
                    disabled={this.state.disabled}
                    type="text"
                    onBlur={e => {
                      this.updateIngredients(e.target.value);
                    }}
                    className="form-control"
                    placeholder="Please enter the ingredients for your recipe"
                  />
                </div>
                <div className="form-group">
                  <label htmlFor="description">Description:</label>
                  <input
                    disabled={this.state.disabled}
                    type="text"
                    onBlur={e => {
                      this.updateDescription(e.target.value);
                    }}
                    className="form-control"
                    placeholder="Please enter the description for your dish"
                  />
                </div>
                <button
                  disabled={this.state.disabled}
                  className="btn dark-space"
                  onClick={() => {
                    this.submit();
                  }}
                >
                  Submit
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

export default withRouter(CreateRecipe);
