import React from "react";
import axios from "axios";

export default class UploadImage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      file: null
    };
  }
  async submit() {
    const formData = new FormData();
    formData.append("myImage", this.state.file);
    const config = {
      headers: {
        "content-type": "multipart/form-data"
      }
    };
    await axios
      .post("http://localhost:8081/upload", formData, config)
      .then(response => {
        alert("Recipe created!");
      })
      .catch(error => {});
  }

  updateImage(files) {
    this.setState({ 
      image: files 
    });
  }

  render() {
    return (
      <form onSubmit={this.onFormSubmit}>
        <label htmlFor="fileupload">Upload Image:</label>
        <br />
        <label id="myImageLabel" htmlFor="myImage" className="btn btn-primary ">
          Choose image
        </label>
        <input
          id="myImage"
          type="file"
          name="myImage"
          accept="image/x-png,image/jpeg"
          onChange={this.onChange}
          onBlur={e => {
            this.updateImage(e.target.files[0]);
          }}
        />
        <button id="uploadButton" className="btn btn-success" type="submit">
          Upload
        </button>
      </form>
    );
  }
}
